// This file handle notifications when app is in background. 
// Note weâ€™ll need to initialize firebase again since service worker has no access to main thread elements.
importScripts("https://www.gstatic.com/firebasejs/8.10.0/firebase-app.js");
importScripts(
  "https://www.gstatic.com/firebasejs/8.10.0/firebase-messaging.js"
);

//remember to change credentials below with your own credentials generated by firebase.
const firebaseConfig = {
  apiKey: '',
  authDomain: '',
  projectId:'' ,
  storageBucket: "",
  messagingSenderId: '',
  appId: '',
  measurementId: ''
};

// Initialize Firebase
firebase.initializeApp(firebaseConfig);

// Retrieve an instance of Firebase Messaging so that it can handle background messages.
const messaging = firebase.messaging();

// Handles background messages when tab is in background.
// If you would like to customize notifications that are received in the background (Web app is closed or not in browser focus) then you should implement this optional method.
messaging.onBackgroundMessage((payload) => {
  console.log(
    "[firebase-messaging-sw.js] Received background message ",
    payload
  );
  const notificationTitle = payload.notification.title;
  const notificationOptions = {
    body: payload.notification.body,
    icon: payload.notification.image,
  };
  
  self.registration.showNotification(notificationTitle, notificationOptions);
});
